    /**  State transitions for names:
            Open -> Auction (startAuction)
            Auction -> Reveal
            Reveal -> Owned
            Reveal -> Open (if nobody bid)
            Owned -> Open (releaseDeed or invalidateName)
    */
    function state(bytes32 _hash) public view override returns (Mode) {
        Entry storage entry = _entries[_hash];

        if (!isAllowed(_hash, block.timestamp)) {
            return Mode.NotYetAvailable;
        } else if (block.timestamp < entry.registrationDate) {
            if (block.timestamp < entry.registrationDate - revealPeriod) {
                return Mode.Auction;
            } else {
                return Mode.Reveal;
            }
        } else {
            if (entry.highestBid == 0) {
                return Mode.Open;
            } else {
                return Mode.Owned;
            }
        }
    }

